class Solution {
    public int maximumCount(int[] nums) {

        int size = nums.length;
        int i = 0;
        int neg = 0;
        for (i = 0; i < size; i++) {
            if (nums[i] < 0) {
                neg++;
            }
            if (nums[i] >= 0) {
                break;
            }
        }
        int zero = 0;
        while (i < size && nums[i] == 0) {
            zero++;
            i++;
        }
        if (neg >= (size - neg - zero)) {
            return neg;
        } else {
            return (size - neg - zero);
        }

    }
}

